import React from "react";
import { useState } from "react";
import Modal from "react-modal";
import { Container, TransactionTypeContainer } from "./styles";
import closeImg from "./close.svg";
import incomeImg from "./income.svg";
import outcomeImg from "./outcome.svg";

export const NewTransactionModal = ({ isOpen, onRequestClose }) => {
  const [ title, setTitle ] = useState('')
  const [ value, setValue ] = useState(0)
  const [ category, setCategory ] = useState('')
  const [type, setType] = useState("deposit");

  const handleCreateNewTransaction = (event) => {
    event.preventDefault();
  }



  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      overlayClassName="react-modal-overlay"
      className="react-modal-content"
    >
      <button
        type="button"
        onClick={onRequestClose}
        className="react-modal-close"
      >
        <img src={closeImg} alt="Fechar modal" />
      </button>

      <Container onSubmit={ handleCreateNewTransaction }>
        <h2>Cadastrar informações</h2>

        <input 
          placeholder="Título" 
        />

        <input type="number" placeholder="Valor" />

        <TransactionTypeContainer>
          <button
            type="button"
            onClick={() => {
              setType("deposit");
            }}
            className={type === "deposit" && "activeDeposit"}
          >
            <img src={incomeImg} alt="Entrada" />
            <span>Entrada</span>
          </button>

          <button
            type="button"
            onClick={() => {
              setType("withdraw");
            }}
            className={type === "withdraw" && "activeWithdraw"}
          >
            <img src={outcomeImg} alt="Saída" />
            <span>Saída</span>
          </button>
        </TransactionTypeContainer>

        <input placeholder="Categoria" />

        <button type="submit">Cadastrar</button>
      </Container>
    </Modal>
  );
};
